// Loop over input items and add a new field called 'myNewField' to the JSON of each one
// ================================
// GitHub Repository Analyzer
// n8n Code Node
// ================================

// Total repositories returned
const totalRepos = items.length;

// Language frequency map
const languages = {};

// Count of recently updated repositories
let recentRepos = 0;

// Optional: GitHub username (if passed from earlier node)
const githubUsername = items[0]?.json?.owner?.login || "Unknown";

// Repositories updated after this date are considered "recent"
const cutoffDate = new Date("2025-10-01");

// Process repositories
for (const item of items) {
  const repo = item.json;

  // Language tracking
  if (repo.language) {
    languages[repo.language] = (languages[repo.language] || 0) + 1;
  }

  // Recent activity check
  const updatedAt = new Date(repo.updated_at);
  if (updatedAt > cutoffDate) {
    recentRepos++;
  }
}

// Profile strength evaluation
let profileStrength = "Weak";

if (totalRepos >= 10 && recentRepos >= 3) {
  profileStrength = "Good";
}

if (totalRepos >= 20 && Object.keys(languages).length >= 3) {
  profileStrength = "Strong";
}

// Build readable report text (for Gmail / AI / logs)
const text = `
GitHub Profile Analysis for ${githubUsername}

Total Repositories: ${totalRepos}
Recently Active Repositories: ${recentRepos}

Languages Used:
${Object.entries(languages)
  .map(([lang, count]) => `- ${lang}: ${count} repos`)
  .join("\n")}

Overall Profile Strength: ${profileStrength}
`;

// ================================
// Final Output (THIS is what n8n sees)
// ================================

return [
  {
    json: {
      github_username: githubUsername,
      totalRepos,
      recentRepos,
      languages,
      profileStrength,
      text
    }
  }
];
